model.dbt_tags_test.orders.raw_code:
{% set payment_methods = ['credit_card', 'coupon', 'bank_transfer', 'gift_card'] %}

with orders as (

    select * from {{ ref('stg_orders') }}

),

payments as (

    select * from {{ ref('stg_payments') }}

),

order_payments as (

    select
        order_id,

        {% for payment_method in payment_methods -%}
        sum(case when payment_method = '{{ payment_method }}' then amount else 0 end) as {{ payment_method }}_amount,
        {% endfor -%}

        sum(amount) as total_amount

    from payments

    group by order_id

),

final as (

    select
        orders.order_id,
        orders.customer_id,
        orders.order_date,
        orders.status,

        {% for payment_method in payment_methods -%}

        order_payments.{{ payment_method }}_amount,

        {% endfor -%}

        order_payments.total_amount as amount

    from orders


    left join order_payments
        on orders.order_id = order_payments.order_id

)

select * from final

model.dbt_tags_test.verify_if_tags_appli.raw_code:
/*
depends on:
- {{ ref('customers') }}
- {{ ref('orders') }}
*/

{{
  config(
    materialized = 'table',
    transient = true
  )
}}

{% set dbt_project_tags = [] %}
{% set tag_name_separator = var('dbt_tags__tag_name_separator','~') %}
{% if execute %}
  {% set dbt_project_tags = dbt_tags.get_dbt_tags(with_value=True) %}
{% endif %}

with dbt_project_tags as (
  {% for item in dbt_project_tags if ".column" in item["level"]  %}

    {% set tag_name = item.tag.split(tag_name_separator)[0] %}
    {% if not item.tag.split(tag_name_separator)[1] %}
      {% set tag_value = item.name %}
    {%- else %}
      {% set tag_value = item.tag.split(tag_name_separator)[1] %}
    {% endif %}
    {{- log("Item:" ~ item ~ " tag_name: " ~ tag_name  ~ " tag_value: " ~ tag_value, info=True) -}}

    select  lower('{{ tag_name }}') as tag_name,
            lower('{{ tag_value }}') as tag_value,
            lower('{{ item.model_fqn }}') as model_name,
            lower('{{ item.name }}') as column_name,

    {% if not loop.last %}
      union all
    {% endif %}

  {% endfor %}
),

column_tag_references as (

  {% for item in dbt_project_tags if ".column" in item.level %}

    select  lower(tag_name) as tag_name,
            lower(tag_value) as tag_value,
            lower(object_database || '.' || object_schema || '.' || object_name) as model_name,
            lower(column_name) as column_name,

    from    table(
      information_schema.tag_references(
        '{{ item.model_fqn }}.{% if item.quote %}{{ adapter.quote(item.name) }}{% else %}{{ item.name }}{% endif %}', 'COLUMN'
      )
    )

    {% if not loop.last %}
      union
    {% endif %}

  {% endfor %}

)

select    distinct
          config.model_name || '.' || config.column_name || ': ' || config.tag_name as config_tag_name,
          config.tag_value as config_tag_value,
          actual.model_name || '.' || actual.column_name || ': ' || actual.tag_name as actual_tag_name,
          actual.tag_value as actual_tag_value
from      dbt_project_tags as config
full join column_tag_references as actual
  on      actual.tag_name     = config.tag_name
  and     actual.tag_value    = config.tag_value
  and     actual.model_name   = config.model_name
  and     actual.column_name  = config.column_name

model.dbt_tags_test.verify_if_masking_po.raw_code:
{{
  config(
    materialized = 'table'
  )
}}


{% if execute %}
    {%- set dbt_project_tags = dbt_tags.get_dbt_tags() %}
{% endif %}
{% set policy_data_types_list = var('dbt_tags__policy_data_types', []) -%}
{%- set ns = dbt_tags.get_resource_ns() %}
{%- set database_name = ns.split('.')[0] %}
{%- set schema_name = ns.split('.')[1] %}

with dbt_project_masking_policies as (

    {%- for item in dbt_project_tags if (".column" in item['level'] and dbt_tags.get_masking_policy_for_tag(item.tag))  %}
    {%- set masking_policy = dbt_tags.get_masking_policy_for_tag(item.tag) %}
        {% for policy_data_types in policy_data_types_list if item.tag in policy_data_types.keys() %}
          {% for datatype in policy_data_types.values() | first %}
            {%- set masking_policy_name = masking_policy.get_name().split('__')[1] ~ "_" ~ datatype %}
    select
        lower('{{ item.tag }}') as tag,
        lower('{{ masking_policy_name }}') as masking_policy_name,
        iff(masking_policy_name = '', null, lower('{{ database_name }}')) as database_name,
        iff(masking_policy_name = '', null, lower('{{ schema_name }}')) as schema_name

            {%- if not loop.last %}
    union all
            {%- endif %}
          {% endfor %}
        {% else %}

        {%- set masking_policy_name = masking_policy.get_name().split('__')[1] if masking_policy else "" %}
    select
        lower('{{ item.tag }}') as tag,
        lower('{{ masking_policy_name }}') as masking_policy_name,
        iff(masking_policy_name = '', null, lower('{{ database_name }}')) as database_name,
        iff(masking_policy_name = '', null, lower('{{ schema_name }}')) as schema_name
          {% endfor %}
        {%- if not loop.last %}
        union all
        {%- endif %}

  {%- endfor %}
),

adapter_masking_policies as (

    {%- if execute -%}
        {%- set query = 'show masking policies in ' ~ database_name ~ '.' ~ schema_name %}
        {%- do run_query(query) %}
        {%- set last_query %}
            select *
            from table(result_scan(last_query_id()))
        {%- endset %}
        {%- set result = run_query(last_query) %}
    {%- endif -%}

    {%- for item in result.rows %}
    select
        lower('{{ item["name"] }}') as masking_policy_name,
        lower('{{ item["database_name"] }}') as database_name,
        lower('{{ item["schema_name"] }}') as schema_name

    {%- if not loop.last %}
      union all
    {%- endif %}

    {%- endfor %}

)

select  distinct
        config.tag as tag,
        config.database_name || '.' || config.schema_name || '.' || config.masking_policy_name as dbt_project_masking_policy,
        actual.database_name || '.' || actual.schema_name || '.' || actual.masking_policy_name as adapter_masking_policy
from      dbt_project_masking_policies as config
full join adapter_masking_policies as actual
  on      actual.database_name     = config.database_name
  and     actual.schema_name    = config.schema_name
  and     actual.masking_policy_name   = config.masking_policy_name

