macro.dbt_tags_test.create_masking_polic.macro_sql:
{% macro create_masking_policy__pii_amount(ns) -%}

  create masking policy if not exists {{ ns }}.pii_amount as (val number)
    returns number ->
    case
      when is_role_in_session('ROLE_DBT_TAGS') or val is null
        then val
      else -99999
    end;

{%- endmacro %}

{% macro create_masking_policy__pii_name(ns) -%}

  create masking policy if not exists {{ ns }}.pii_name as (val string)
    returns string ->
    case
      when is_role_in_session('ROLE_DBT_TAGS') or coalesce(val, '') = ''
        then val
      else sha2(val)
    end;

{%- endmacro %}

